const forkAndImpersonateAtBlock = async (block: number, account: string) => {
    await network.provider.request({
        method: "hardhat_reset",
        params: [
            {
                forking: {
                    jsonRpcUrl: process.env.ARCHIVE_NODE_URL_L2,
                    blockNumber: block,
                },
            },
        ],
    });

    await network.provider.request({
        method: "hardhat_impersonateAccount",
        params: [account],
    });
};

